import React from "react";
import { Button, Typography } from "@mui/material";
import PropTypes from "prop-types";
import { styled } from "@mui/material/styles";
import HowToVoteIcon from "@mui/icons-material/HowToVote";
import Container from "@mui/material/Container";
import Box from "@mui/material/Box";
import { useNavigate } from "react-router-dom";

// Update the backgroundImage variable to use the local image.
const backgroundImage = require("./StartPage.png").default;

const CoverLayoutRoot = styled("section")(({ theme }) => ({
  color: theme.palette.common.white,
  position: "relative",
  display: "flex",
  alignItems: "center",
  [theme.breakpoints.up("sm")]: {
    height: "100vh",
    minHeight: 500,
    maxHeight: 1300,
  },
}));

const Background = styled(Box)({
  position: "absolute",
  left: 0,
  right: 0,
  top: 0,
  bottom: 0,
  zIndex: -2,
});

export default function CoverPage() {
  const navigate = useNavigate();

  const handleClick = () => {
    console.log("home button clicked");
    navigate("/home");
    window.location.reload();
  };

  return (
    <CoverLayoutRoot>
      <Container
        sx={{
          mt: 3,
          mb: 14,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <HowToVoteIcon style={{ fontSize: 64 }} />
        <Typography color="inherit" align="center" variant="h2" marked="center">
          Blockchain Based Election
        </Typography>
        <Typography
          color="inherit"
          align="center"
          variant="h5"
          sx={{ mb: 4, mt: { sx: 4, sm: 10 } }}
        >

The primary goal of this project is to make use of blockchain technology to construct a voting mechanism. The project seeks to offer transparent and secure environment for carrying out various voting procedures. Blockchain is used by the distributed system to guarantee integrity and immutability of the vote records.
        </Typography>
        <Button
          color="primary"
          variant="contained"
          size="large"
          sx={{ minWidth: 200 }}
          onClick={handleClick}
        >
          Enter the Voting System Home Page
        </Button>
        <Box
          sx={{
            position: "absolute",
            left: 0,
            right: 0,
            top: 0,
            bottom: 0,
            backgroundColor: "#006400", // Dark green color
            opacity: 0.5,
            zIndex: -1,
          }}
        />
        <Background sx={{ backgroundImage: `url(${backgroundImage})`, backgroundColor: "#7fc7d9", backgroundPosition: "center" }} />
      </Container>
    </CoverLayoutRoot>
  );
}

CoverPage.propTypes = {
  children: PropTypes.node,
};
