import { useState, useEffect } from "react";
import Box from "@mui/material/Box";
import Grid from "@mui/material/Grid";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import Divider from "@mui/material/Divider";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import Card from "@mui/material/Card";
import CardHeader from "@mui/material/CardHeader";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import TextField from "@mui/material/TextField";
import { Stack } from "@mui/material";
import candidateImage from "./candidate.png";

export default function Admin({ role, contract, web3, currentAccount }) {
  const [electionState, setElectionState] = useState(0);
  const [loading, setLoading] = useState(true);
  const [candidates, setCandidates] = useState([]);
  const [candidateName, setCandidateName] = useState("");
  const [voterAddress, setVoterAddress] = useState("");
  const [open, setOpen] = useState(false);

  // Function to fetch candidates from the smart contract
  const getCandidates = async () => {
    if (contract) {
      const count = await contract.methods.candidatesCount().call();
      const temp = [];
      for (let i = 0; i < count; i++) {
        const candidate = await contract.methods.getCandidateDetails(i).call();
        temp.push({ name: candidate[0], votes: candidate[1] });
      }
      setCandidates(temp);
      setLoading(false);
    }
  };

  // Function to fetch election state from the smart contract
  const getElectionState = async () => {
    if (contract) {
      const state = await contract.methods.electionState().call();
      setElectionState(parseInt(state));
    }
  };

  useEffect(() => {
    getElectionState();
    getCandidates();
  }, [contract]);

  // Function to handle the start/end election button click
  const handleEnd = () => {
    setOpen(true);
  };

  // Function to handle the close dialog
  const handleClose = () => {
    setOpen(false);
  };

  // Function to handle the agree button click in the dialog
  const handleAgree = async () => {
    if (electionState === 0) {
      try {
        if (contract) {
          await contract.methods.startElection().send({ from: currentAccount });
          getElectionState();
        }
      } catch (error) {
        console.error("Error:", error);
      }
    } else if (electionState === 1) {
      try {
        if (contract) {
          await contract.methods.endElection().send({ from: currentAccount });
          getElectionState();
        }
      } catch (error) {
        console.error("Error:", error);
      }
    }

    setOpen(false);
  };

  // Function to handle the form submission for adding a candidate
  const handleCandidateForm = async (event) => {
    event.preventDefault();
    try {
      await contract.methods.addCandidate(candidateName).send({ from: currentAccount });
      getCandidates(); // Refresh candidates after adding a new one
    } catch (error) {
      console.log(error);
    }
    setCandidateName("");
  };

  // Function to handle the form submission for adding a voter
  const handleVoterForm = async (event) => {
    event.preventDefault();
    try {
      await contract.methods.addVoter(voterAddress).send({ from: currentAccount });
    } catch (error) {
      console.log(error);
    }
    setVoterAddress("");
  };

  // Function to handle changes in the candidate name input field
  const handleNameChange = (event) => {
    setCandidateName(event.target.value);
  };

  // Function to handle changes in the voter address input field
  const handleAddressChange = (event) => {
    setVoterAddress(event.target.value);
  };

  return (
    <Box>
      {loading ? (
        <Box
          sx={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            height: "80vh",
          }}
        >
          Loading...
        </Box>
      ) : (
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <Typography variant="h5" align="center" gutterBottom>
              Election Status:{" "}
              {electionState === 0 && "Not Started"}
              {electionState === 1 && "In Progress"}
              {electionState === 2 && "Ended"}
            </Typography>
            <Divider />
          </Grid>
          {electionState !== 2 && (
            <Grid item xs={12}>
              <Button variant="contained" color="primary" fullWidth onClick={handleEnd}>
                {electionState === 0 ? "Start Election" : "End Election"}
              </Button>
            </Grid>
          )}

          {/* Add Voters / Candidates Section */}
          {electionState === 0 && (
            <>
              <Grid item xs={12}>
                <Typography variant="h5" align="center" gutterBottom>
                  Add Voters / Candidates
                </Typography>
                <Divider />
              </Grid>
              {/* Add Voter Form */}
              <Grid item xs={12} md={6}>
                <form onSubmit={handleVoterForm}>
                  <Stack spacing={2} sx={{ p: 2 }}>
                    <TextField
                      label="Voter Address"
                      variant="outlined"
                      fullWidth
                      value={voterAddress}
                      onChange={handleAddressChange}
                    />
                    <Button type="submit" variant="contained" color="primary" fullWidth>
                      Add Voter
                    </Button>
                  </Stack>
                </form>
              </Grid>
              {/* Add Candidate Form */}
              <Grid item xs={12} md={6}>
                <form onSubmit={handleCandidateForm}>
                  <Stack spacing={2} sx={{ p: 2 }}>
                    <TextField
                      label="Candidate Name"
                      variant="outlined"
                      fullWidth
                      value={candidateName}
                      onChange={handleNameChange}
                    />
                    <Button type="submit" variant="contained" color="primary" fullWidth>
                      Add Candidate
                    </Button>
                  </Stack>
                </form>
              </Grid>
            </>
          )}

          {/* Candidates Section */}
          {electionState > 0 && (
            <Grid item xs={12}>
              <Typography variant="h5" align="center" gutterBottom>
                Candidates
              </Typography>
              <Divider />
              <Grid container spacing={2} justifyContent="center">
                {candidates.map((candidate, index) => (
                  <Grid item key={index} xs={12} sm={6} md={4} lg={3}>
                    <Card>
                      <CardHeader title={candidate.name} />
                      <CardMedia
                        component="img"
                        image={candidateImage}
                        alt={candidate.name}
                      />
                      <CardContent>
                        <Typography variant="body2" color="textSecondary">
                          {candidate.votes} votes
                        </Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Grid>
          )}
        </Grid>
      )}

      {/* Confirmation Dialog */}
      <Dialog open={open} onClose={handleClose}>
        <DialogContent>
          <DialogContentText>
            {electionState === 0 ? "Do you want to start the election?" : "Do you want to end the election?"}
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="secondary">
            Cancel
          </Button>
          <Button onClick={handleAgree} color="primary" autoFocus>
            Agree
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}
