import React from "react";
import { Button, Typography, Container, Box } from "@mui/material";
import PropTypes from "prop-types";
import { styled } from "@mui/material/styles";
import HowToVoteIcon from "@mui/icons-material/HowToVote";
import { Link } from "react-router-dom";

// Update the backgroundImage variable to use the local image.
const backgroundImage = require("./StartPage.png").default;

const CoverLayoutRoot = styled("section")(({ theme }) => ({
  color: theme.palette.common.white,
  position: "relative",
  display: "flex",
  alignItems: "center",
  minHeight: "100vh",
  textAlign: "center",
}));

const Background = styled(Box)({
  position: "absolute",
  left: 0,
  right: 0,
  top: 0,
  bottom: 0,
  zIndex: -2,
});

const GetStartedButton = styled(Button)(({ theme }) => ({
  margin: theme.spacing(2),
  padding: theme.spacing(2),
  fontSize: "1.2rem",
  fontWeight: 700,
}));

const CoverPage = () => {
  return (
    <CoverLayoutRoot>
      <Container maxWidth="md">
        <HowToVoteIcon sx={{ fontSize: 64, mb: 2 }} />
        <Typography variant="h2" component="h1" gutterBottom>
          Blockchain-Based Voting System
        </Typography>
        <Typography variant="body1" paragraph>
          Welcome to the future of voting! Our blockchain-based voting system
          ensures transparency, security, and integrity in every vote. Join us
          in shaping the future of democracy.
        </Typography>
        <Link to="/home" style={{ textDecoration: "none" }}>
          <GetStartedButton variant="contained" color="primary">
            Get Started
          </GetStartedButton>
        </Link>
        <Background
          sx={{
            backgroundImage: `url(${backgroundImage})`,
            backgroundColor: "#7fc7d9",
            backgroundPosition: "center",
          }}
        />
      </Container>
    </CoverLayoutRoot>
  );
};

CoverPage.propTypes = {
  children: PropTypes.node,
};

export default CoverPage;
